var target = dm_Tools.getChild(node.parent, 'Parcelles') ;
if(target) {
    var aggregatedSigma = {} ;
    dm_Tools.scanNodes(node, function(n) {
        if(!n.children) {
            if(!aggregatedSigma[n.name]) aggregatedSigma[n.name] = 0 ;
            aggregatedSigma[n.name] += n.rawNode.sigma ;
        }
    })
    console.log('aggregated sigmas : '+JSON.stringify(aggregatedSigma)) ;

    var impMin = Number.MAX_VALUE ;
    var impMax = 0 ;
    dm_Tools.scanNodes(target, function(n) {
        if(!n.children) {
            var found = false ;
            var parent = n ;
            while(!found && parent) {
                found = aggregatedSigma[parent.name] ;
                if(!found) {
                    for(var p in aggregatedSigma) {
                        if(!found && ((''+parent.name).indexOf(p) == 0)) {
                            found = aggregatedSigma[p] ;
                        }
                    }
                }
                parent = parent.parent ;
            }
            if(found) {
                n.rawNode.importance = n.rawNode.surf_eng * found ;
                impMin = Math.min(impMin, n.rawNode.importance) ;
                impMax = Math.max(impMax, n.rawNode.importance) ;
            }
        }
    }) ;

    dm_Tools.digNodes(target, null, function(n) {
        if(!n.children && n.rawNode.importance && (impMax > impMin)) {
            n.rawNode.importance = 100 * (n.rawNode.importance - impMin) / (impMax - impMin) ;
        } else {
            var child ;
            var impChildrenMax = 0 ;
            for(var i=0; i<n.children.length; i++) {
                child = n.children[i] ; 
                if(child.rawNode && child.rawNode.importance) {
                    impChildrenMax = Math.max(impChildrenMax, child.rawNode.importance) ;
                }
            }
            n.rawNode.importance = impChildrenMax ;
        }
        n.rawNode['gauge_1.dm_props'] = {
            'value': n.rawNode.importance,
            'max': 100,
            'invertColors': false,
            'y': -21
        }
    }) ;
}

